<template>
  <div class="welcome-screen">
    <!-- 상단 텍스트 -->
    <div class="header-text">
      <h1 :class="{ 'show': showFirstLine }">내가 등록한 반려동물</h1>
    </div>

    <!-- 프로필 카드 슬라이더 섹션 -->
    <div class="profile-slider-section">
      <button class="slider-button prev" @click="prevCard">&lt;</button>
      
      <div class="profile-section">
        <div 
          class="profile-card" 
          ref="card"
          :data-sliding="slideDirection"
          @mousemove="handleMouseMove"
          @mouseleave="handleMouseLeave"
        >
          <div class="card-content">
            <div class="image-container">
              <img :src="currentProfile.image" :alt="currentProfile.name" class="profile-image"/>
            </div>
            <div class="profile-info">
              <h2 class="pet-name">{{ currentProfile.name }}</h2>
              <p class="pet-details">{{ currentProfile.age }} · {{ currentProfile.breed }}</p>
            </div>
          </div>
        </div>
      </div>

      <button class="slider-button next" @click="nextCard">&gt;</button>
    </div>

    <!-- 하단 버튼 -->
    <div class="bottom-section">
      <button :class="{ 'show': showButton }" @click="goToNext" class="start-button">
        돌아가기
      </button>
    </div>
  </div>
</template>

<script setup>
import { ref, onMounted } from 'vue';
import { useRouter } from 'vue-router';

const router = useRouter();
const card = ref(null);
const currentIndex = ref(0);
const isSliding = ref(false);
const slideDirection = ref('');

// 샘플 프로필 데이터 - 이미지 경로 수정
const profiles = [
  { id: 1, name: '멍뭉이', age: '2살', breed: '말티즈', image: 'data:image/jpeg;base64,' },
  { id: 2, name: '까미', age: '3살', breed: '포메라니안', image: 'data:image/jpeg;base64,' },
  { id: 3, name: '초코', age: '1살', breed: '푸들', image: 'data:image/jpeg;base64,' },
  { id: 4, name: '루시', age: '4살', breed: '치와와', image: 'data:image/jpeg;base64,'},
  { id: 5, name: '바니', age: '2살', breed: '비숑', image: 'https://blog.kakaocdn.net/dn/IhP0p/btsbk2mLKw7/oQsHFAWYmpqxQdR8YXn4t1/img.jpg' },
];

const currentProfile = ref(profiles[0]);

// 애니메이션을 위한 변수
const showFirstLine = ref(false);
const showButton = ref(false);

// 슬라이더 컨트롤
const nextCard = () => {
  if (isSliding.value) return;
  isSliding.value = true;
  slideDirection.value = 'exit-right'; // 현재 카드를 오른쪽으로 보내기
  
  // 현재 카드가 완전히 사라진 후에 새 카드 준비
  setTimeout(() => {
    currentIndex.value = (currentIndex.value + 1) % profiles.length;
    currentProfile.value = profiles[currentIndex.value];
    slideDirection.value = 'ready-left'; // 새 카드를 왼쪽에 대기
    
    // 약간의 지연 후 새 카드 진입 시작
    requestAnimationFrame(() => {
      slideDirection.value = 'enter-from-left';
      
      // 애니메이션이 끝나면 상태 초기화
      setTimeout(() => {
        slideDirection.value = '';
        isSliding.value = false;
      }, 500);
    });
  }, 500);
};

const prevCard = () => {
  if (isSliding.value) return;
  isSliding.value = true;
  slideDirection.value = 'exit-left'; // 현재 카드를 왼쪽으로 보내기
  
  setTimeout(() => {
    currentIndex.value = (currentIndex.value - 1 + profiles.length) % profiles.length;
    currentProfile.value = profiles[currentIndex.value];
    slideDirection.value = 'ready-right'; // 새 카드를 오른쪽에 대기
    
    requestAnimationFrame(() => {
      slideDirection.value = 'enter-from-right';
      
      setTimeout(() => {
        slideDirection.value = '';
        isSliding.value = false;
      }, 500);
    });
  }, 500);
};

onMounted(() => {
  setTimeout(() => { showFirstLine.value = true; }, 1000);
  setTimeout(() => { showButton.value = true; }, 3000);
});

const goToNext = () => {
  router.push('/home');
};

const handleMouseMove = (event) => {
  if (isSliding.value) return;
  
  const card = event.currentTarget;
  const rect = card.getBoundingClientRect();
  const x = event.clientX - rect.left; // 마우스의 X 위치
  const y = event.clientY - rect.top;  // 마우스의 Y 위치
  
  // 카드 중심을 기준으로 -1 ~ 1 사이의 값으로 변환
  const xRotation = ((y - rect.height / 2) / rect.height) * 20; // 최대 20도 회전
  const yRotation = ((x - rect.width / 2) / rect.width) * 20;
  
  card.style.transform = `
    perspective(1000px)
    rotateX(${-xRotation}deg)
    rotateY(${yRotation}deg)
    scale3d(1.05, 1.05, 1.05)
  `;
};

const handleMouseLeave = (event) => {
  if (isSliding.value) return;
  const card = event.currentTarget;
  card.style.transform = '';
};
</script>

