/* ready 상태일 때는 트랜지션 즉시 비활성화 */
.profile-card[data-sliding="ready-right"],
.profile-card[data-sliding="ready-left"] {
  transition: none !important;
}

/* 호버 시 그림자 효과 강화 */
.profile-card:hover {
